import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { pollsAPI } from "../services/api";

const VoterDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [ongoingPolls, setOngoingPolls] = useState([]);
  const [pastPolls, setPastPolls] = useState([]);
  const [selectedPoll, setSelectedPoll] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [activeTab, setActiveTab] = useState("ongoing");

  // Fetch polls on component mount
  useEffect(() => {
    fetchPolls();
  }, []);

  // Fetch ongoing and past polls
  const fetchPolls = async () => {
    try {
      setLoading(true);
      setError("");
      
      // Fetch ongoing polls
      const ongoingResponse = await pollsAPI.getOngoingPolls();
      setOngoingPolls(ongoingResponse.data);
      
      // Fetch past polls
      const pastResponse = await pollsAPI.getPastPolls();
      setPastPolls(pastResponse.data);
      
    } catch (err) {
      console.error("Error fetching polls:", err);
      setError("Failed to load polls. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Load poll details
  const loadPollDetails = async (pollId) => {
    try {
      setLoading(true);
      setSelectedOption(null);
      setError("");
      const response = await pollsAPI.getPollDetails(pollId);
      setSelectedPoll(response.data);
    } catch (err) {
      console.error("Error loading poll details:", err);
      setError("Failed to load poll details");
    } finally {
      setLoading(false);
    }
  };

  // Handle vote submission
  const handleVote = async () => {
    if (!selectedPoll || !selectedOption) {
      setError("Please select an option to vote");
      return;
    }

    try {
      setLoading(true);
      setError("");
      await pollsAPI.vote(selectedPoll.id, selectedOption);
      
      setSuccessMessage("Your vote has been recorded successfully!");
      
      // Refresh poll details to update the hasVoted status
      await loadPollDetails(selectedPoll.id);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage("");
      }, 3000);
    } catch (err) {
      console.error("Error submitting vote:", err);
      setError(err.response?.data?.error || "Failed to submit your vote");
    } finally {
      setLoading(false);
    }
  };

  // Go back to polls list
  const handleBackToPollsList = () => {
    setSelectedPoll(null);
    setSelectedOption(null);
  };

  // View poll results
  const viewPollResults = async (pollId) => {
    try {
      setLoading(true);
      setError("");
      const response = await pollsAPI.getResults(pollId);
      console.log("Poll results:", response.data);
      loadPollDetails(pollId);
    } catch (err) {
      console.error("Error fetching poll results:", err);
      setError("Failed to load poll results");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="voter-dashboard">
      <header className="dashboard-header">
        <h2>Voter Dashboard</h2>
        <div className="user-info">
          <span>Welcome, {currentUser.name}</span>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </header>

      {error && <div className="error-message">{error}</div>}
      {successMessage && <div className="success-message">{successMessage}</div>}

      {loading && <div className="loading">Loading...</div>}

      {!loading && !selectedPoll && (
        <div className="polls-container">
          <div className="polls-tabs">
            <div className="tabs-header">
              <button 
                className={activeTab === "ongoing" ? "active" : ""}
                onClick={() => setActiveTab("ongoing")}
              >
                Ongoing Polls
              </button>
              <button 
                className={activeTab === "past" ? "active" : ""}
                onClick={() => setActiveTab("past")}
              >
                Past Polls
              </button>
            </div>
          </div>

          {activeTab === "ongoing" && (
            <div className="polls-list">
              <h3>Ongoing Polls</h3>
              
              {ongoingPolls.length === 0 && (
                <p>No active polls available at the moment.</p>
              )}

              {ongoingPolls.map(poll => (
                <div key={poll.id} className="poll-item ongoing">
                  <div className="poll-info">
                    <h4>{poll.title}</h4>
                    <p className="poll-description">{poll.description}</p>
                    {poll.question && (
                      <p className="poll-question"><strong>Question:</strong> {poll.question}</p>
                    )}
                    <div className="vote-count">
                      <span>Total votes: {poll.vote_count || 0}</span>
                    </div>
                  </div>
                  
                  <div className="poll-actions">
                    <button 
                      onClick={() => loadPollDetails(poll.id)}
                      className="view-poll-btn"
                    >
                      Vote Now
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}

          {activeTab === "past" && (
            <div className="polls-list">
              <h3>Past Polls</h3>
              
              {pastPolls.length === 0 && (
                <p>No past polls available.</p>
              )}

              {pastPolls.map(poll => (
                <div key={poll.id} className="poll-item ended">
                  <div className="poll-info">
                    <h4>{poll.title}</h4>
                    <p className="poll-description">{poll.description}</p>
                    {poll.question && (
                      <p className="poll-question"><strong>Question:</strong> {poll.question}</p>
                    )}
                    <div className="poll-dates">
                      <span>Ended: {new Date(poll.end_date).toLocaleString()}</span>
                    </div>
                    <div className="vote-count">
                      <span>Total votes: {poll.vote_count || 0}</span>
                    </div>
                  </div>
                  
                  <div className="poll-actions">
                    <button 
                      onClick={() => viewPollResults(poll.id)}
                      className="view-results-btn"
                    >
                      View Results
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {!loading && selectedPoll && (
        <div className="poll-details">
          <button 
            onClick={handleBackToPollsList}
            className="back-btn"
          >
            &larr; Back to Polls
          </button>

          <h3>{selectedPoll.title}</h3>
          <p className="poll-description">{selectedPoll.description}</p>
          
          <div className="poll-question-box">
            <h4>{selectedPoll.question || "Please cast your vote:"}</h4>
          </div>

          {selectedPoll.hasVoted ? (
            <div className="already-voted">
              <p>You have already voted in this poll.</p>
              <button 
                onClick={() => viewPollResults(selectedPoll.id)}
                className="view-results-btn"
              >
                View Results
              </button>
            </div>
          ) : (
            <div className="voting-area">              
              <div className="options-list">
                {selectedPoll.options && selectedPoll.options.map(option => (
                  <div 
                    key={option.id} 
                    className={`option-item ${selectedOption === option.id ? 'selected' : ''}`}
                    onClick={() => setSelectedOption(option.id)}
                  >
                    <div className="option-info">
                      <span>{option.option_text}</span>
                    </div>
                    <div className="option-selector">
                      <input 
                        type="radio" 
                        name="option" 
                        checked={selectedOption === option.id}
                        onChange={() => setSelectedOption(option.id)}
                      />
                    </div>
                  </div>
                ))}
              </div>
              
              <button 
                onClick={handleVote}
                className="vote-btn"
                disabled={loading || !selectedOption}
              >
                {loading ? "Processing..." : "Submit Vote"}
              </button>
            </div>
          )}

          {selectedPoll.hasVoted && selectedPoll.options && (
            <div className="results-preview">
              <h4>Current Results:</h4>
              <div className="results-list">
                {selectedPoll.options.map(option => {
                  const votePercentage = selectedPoll.vote_count > 0 
                    ? (option.votes / selectedPoll.vote_count) * 100 
                    : 0;
                  
                  return (
                    <div key={option.id} className="result-item">
                      <div className="result-info">
                        <span className="option-text">{option.option_text}</span>
                        <div className="vote-bar">
                          <div 
                            className="vote-progress" 
                            style={{ width: `${votePercentage}%` }}
                          ></div>
                        </div>
                        <div className="vote-stats">
                          <span>{option.votes} votes</span>
                          <span>{votePercentage.toFixed(1)}%</span>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
