import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../services/api";

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState("ongoing");
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [ongoingPolls, setOngoingPolls] = useState([]);
  const [pastPolls, setPastPolls] = useState([]);
  const [pendingPolls, setPendingPolls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  
  // Poll form state
  const [pollTitle, setPollTitle] = useState("");
  const [pollDescription, setPollDescription] = useState("");
  const [pollQuestion, setPollQuestion] = useState("");
  const [pollOptions, setPollOptions] = useState(["", ""]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const fetchPolls = async () => {
    setLoading(true);
    setError(null);
    try {
      const ongoingRes = await api.get("/polls/ongoing");
      const pastRes = await api.get("/polls/past");
      const pendingRes = await api.get("/polls/pending");
      
      setOngoingPolls(ongoingRes.data);
      setPastPolls(pastRes.data);
      setPendingPolls(pendingRes.data);
    } catch (err) {
      console.error("Error fetching polls:", err);
      setError("Failed to load polls. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPolls();
    // Poll for updates every minute
    const intervalId = setInterval(fetchPolls, 60000);
    return () => clearInterval(intervalId);
  }, []);

  const handleCreatePoll = async (e) => {
    e.preventDefault();
    
    // Validate form
    if (pollTitle.trim().length < 5) {
      setError("Poll title must be at least 5 characters");
      return;
    }
    
    if (!pollDescription.trim() || !pollQuestion.trim()) {
      setError("Description and question are required");
      return;
    }
    
    // Filter out empty options
    const filteredOptions = pollOptions.filter(opt => opt.trim() !== "");
    if (filteredOptions.length < 2) {
      setError("At least 2 options are required");
      return;
    }

    if (!startDate || !endDate) {
      setError("Start and end dates are required");
      return;
    }

    if (new Date(endDate) <= new Date(startDate)) {
      setError("End date must be after start date");
      return;
    }

    setLoading(true);
    setError(null);
    
    const pollData = {
      title: pollTitle,
      description: pollDescription,
      question: pollQuestion,
      options: filteredOptions,
      start_date: startDate,
      end_date: endDate,
    };
    
    try {
      console.log("Sending poll data:", pollData);
      const response = await api.post("/polls", pollData);
      console.log("Poll created successfully:", response.data);
      setSuccess("Poll created successfully!");
      
      // Reset form
      setPollTitle("");
      setPollDescription("");
      setPollQuestion("");
      setPollOptions(["", ""]);
      setStartDate("");
      setEndDate("");
      setShowCreateForm(false);
      
      // Refresh polls list
      fetchPolls();
    } catch (err) {
      console.error("Error creating poll:", err.response?.data?.message || err.message);
      setError(err.response?.data?.message || "Failed to create poll. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...pollOptions];
    newOptions[index] = value;
    setPollOptions(newOptions);
  };

  const addOption = () => {
    setPollOptions([...pollOptions, ""]);
  };

  const removeOption = (index) => {
    if (pollOptions.length > 2) {
      const newOptions = [...pollOptions];
      newOptions.splice(index, 1);
      setPollOptions(newOptions);
    }
  };

  const startPoll = async (pollId) => {
    try {
      await api.put(`/polls/${pollId}/start`);
      setSuccess("Poll started successfully");
      fetchPolls();
    } catch (err) {
      setError("Failed to start poll");
      console.error(err);
    }
  };

  const endPoll = async (pollId) => {
    try {
      await api.put(`/polls/${pollId}/end`);
      setSuccess("Poll ended successfully");
      fetchPolls();
    } catch (err) {
      setError("Failed to end poll");
      console.error(err);
    }
  };

  // Calculate time remaining for a poll
  const getTimeRemaining = (endDate) => {
    const total = Date.parse(endDate) - Date.parse(new Date());
    if (total <= 0) return "Ended";
    
    const seconds = Math.floor((total / 1000) % 60);
    const minutes = Math.floor((total / 1000 / 60) % 60);
    const hours = Math.floor((total / (1000 * 60 * 60)) % 24);
    const days = Math.floor(total / (1000 * 60 * 60 * 24));
    
    if (days > 0) return `${days} day${days !== 1 ? "s" : ""} remaining`;
    if (hours > 0) return `${hours} hour${hours !== 1 ? "s" : ""} remaining`;
    if (minutes > 0) return `${minutes} minute${minutes !== 1 ? "s" : ""} remaining`;
    return `${seconds} second${seconds !== 1 ? "s" : ""} remaining`;
  };

  const formatDate = (dateString) => {
    const options = { 
      year: "numeric", 
      month: "short", 
      day: "numeric",
      hour: "2-digit", 
      minute: "2-digit"
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Renders the appropriate polls based on the active tab
  const renderPolls = () => {
    let polls = [];
    
    if (activeTab === "ongoing") polls = ongoingPolls;
    else if (activeTab === "past") polls = pastPolls;
    else if (activeTab === "pending") polls = pendingPolls;
    
    if (loading && polls.length === 0) {
      return <div className="loading">Loading polls...</div>;
    }
    
    if (polls.length === 0) {
      return <div>No {activeTab} polls found.</div>;
    }
    
    return (
      <div className="polls-list">
        <h3>{activeTab.charAt(0).toUpperCase() + activeTab.slice(1)} Polls</h3>
        {polls.map(poll => (
          <div key={poll.id} className={`poll-item ${poll.status}`}>
            <div className="poll-info">
              <h4>{poll.title}</h4>
              <p className="poll-description">{poll.description}</p>
              <div className="poll-question">{poll.question}</div>
              <div className="poll-dates">
                <span>Start: {formatDate(poll.start_date)}</span>
                <span>End: {formatDate(poll.end_date)}</span>
                {activeTab === "ongoing" && 
                  <span>{getTimeRemaining(poll.end_date)}</span>
                }
              </div>
              {poll.vote_count !== undefined && 
                <div className="vote-count">{poll.vote_count} vote{poll.vote_count !== 1 ? "s" : ""}</div>
              }
            </div>
            <div className="poll-actions">
              {activeTab === "pending" && (
                <button 
                  className="start-poll-btn"
                  onClick={() => startPoll(poll.id)}
                >
                  Start Poll
                </button>
              )}
              {activeTab === "ongoing" && (
                <button 
                  className="end-poll-btn"
                  onClick={() => endPoll(poll.id)}
                >
                  End Poll
                </button>
              )}
              <button className="view-results-btn">
                View Results
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <div className="user-info">
          <span>Welcome, {user?.name || "Admin"}</span>
          <button className="logout-btn" onClick={logout}>
            Logout
          </button>
        </div>
      </div>

      {error && (
        <div className="error-message" onClick={() => setError(null)}>
          {error}
        </div>
      )}
      
      {success && (
        <div className="success-message" onClick={() => setSuccess(null)}>
          {success}
        </div>
      )}

      <div className="admin-actions">
        <button 
          className="create-poll-btn"
          onClick={() => setShowCreateForm(!showCreateForm)}
        >
          {showCreateForm ? "Cancel" : "Create New Poll"}
        </button>
      </div>

      {showCreateForm && (
        <div className="create-poll-form">
          <h3>Create New Poll</h3>
          <form onSubmit={handleCreatePoll}>
            <div className="form-group">
              <label htmlFor="pollTitle">Poll Title</label>
              <input
                type="text"
                id="pollTitle"
                value={pollTitle}
                onChange={(e) => setPollTitle(e.target.value)}
                placeholder="Enter poll title (min 5 characters)"
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="pollDescription">Description</label>
              <textarea
                id="pollDescription"
                value={pollDescription}
                onChange={(e) => setPollDescription(e.target.value)}
                placeholder="Enter poll description"
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="pollQuestion">Question</label>
              <input
                type="text"
                id="pollQuestion"
                value={pollQuestion}
                onChange={(e) => setPollQuestion(e.target.value)}
                placeholder="Enter the question for voters"
                required
              />
            </div>
            
            <div className="form-group">
              <label>Options (at least 2)</label>
              {pollOptions.map((option, index) => (
                <div key={index} className="option-input-group">
                  <input
                    type="text"
                    value={option}
                    onChange={(e) => handleOptionChange(index, e.target.value)}
                    placeholder={`Option ${index + 1}`}
                    required={index < 2}
                  />
                  {index >= 2 && (
                    <button
                      type="button"
                      className="remove-option-btn"
                      onClick={() => removeOption(index)}
                    >
                      Ã—
                    </button>
                  )}
                </div>
              ))}
              <button
                type="button"
                className="add-option-btn"
                onClick={addOption}
              >
                + Add Option
              </button>
            </div>
            
            <div className="form-group">
              <label htmlFor="startDate">Start Date</label>
              <input
                type="datetime-local"
                id="startDate"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                min={new Date().toISOString().slice(0, 16)}
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="endDate">End Date</label>
              <input
                type="datetime-local"
                id="endDate"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                min={startDate || new Date().toISOString().slice(0, 16)}
                required
              />
            </div>
            
            <button type="submit" className="submit-poll-btn" disabled={loading}>
              {loading ? "Creating..." : "Create Poll"}
            </button>
          </form>
        </div>
      )}

      <div className="polls-tabs">
        <div className="tabs-header">
          <button
            className={activeTab === "ongoing" ? "active" : ""}
            onClick={() => setActiveTab("ongoing")}
          >
            Ongoing Polls
          </button>
          <button
            className={activeTab === "pending" ? "active" : ""}
            onClick={() => setActiveTab("pending")}
          >
            Pending Polls
          </button>
          <button
            className={activeTab === "past" ? "active" : ""}
            onClick={() => setActiveTab("past")}
          >
            Past Polls
          </button>
        </div>
        
        {renderPolls()}
      </div>
    </div>
  );
};

export default AdminDashboard;
