import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { pollsAPI } from "../services/api";

const AdminDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [allPolls, setAllPolls] = useState([]);
  const [pendingPolls, setPendingPolls] = useState([]);
  const [ongoingPolls, setOngoingPolls] = useState([]);
  const [endedPolls, setEndedPolls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [activeTab, setActiveTab] = useState("ongoing");
  
  // New poll form state
  const [newPoll, setNewPoll] = useState({
    title: "",
    description: "",
    question: "",
    options: ["", ""] // Start with two empty options
  });

  // Fetch polls on component mount
  useEffect(() => {
    fetchAllPolls();
  }, []);

  // Fetch all polls
  const fetchAllPolls = async () => {
    try {
      setLoading(true);
      const response = await pollsAPI.getAllPolls();
      setAllPolls(response.data);
      
      // Split polls by status
      setPendingPolls(response.data.filter(poll => poll.status === "pending"));
      setOngoingPolls(response.data.filter(poll => poll.status === "ongoing"));
      setEndedPolls(response.data.filter(poll => poll.status === "ended").slice(0, 3));
    } catch (err) {
      console.error("Error fetching polls:", err);
      setError("Failed to load polls. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPoll({ ...newPoll, [name]: value });
  };

  // Handle option changes
  const handleOptionChange = (index, value) => {
    const updatedOptions = [...newPoll.options];
    updatedOptions[index] = value;
    setNewPoll({ ...newPoll, options: updatedOptions });
  };

  // Add new option field
  const addOption = () => {
    setNewPoll({ ...newPoll, options: [...newPoll.options, ""] });
  };

  // Remove option field
  const removeOption = (index) => {
    if (newPoll.options.length <= 2) {
      setError("At least two options are required");
      return;
    }
    
    const updatedOptions = newPoll.options.filter((_, i) => i !== index);
    setNewPoll({ ...newPoll, options: updatedOptions });
  };

  // Create new poll
  const handleCreatePoll = async (e) => {
    e.preventDefault();
    
    // Validate options are not empty
    const emptyOptions = newPoll.options.some(option => option.trim() === "");
    if (emptyOptions) {
      setError("All options must be filled in");
      return;
    }
    
    try {
      setLoading(true);
      await pollsAPI.createPoll(newPoll);
      
      // Reset form and fetch updated polls
      setNewPoll({
        title: "",
        description: "",
        question: "",
        options: ["", ""] // Reset to two empty options
      });
      setShowCreateForm(false);
      fetchAllPolls();
    } catch (err) {
      console.error("Error creating poll:", err);
      setError(err.response?.data?.error || "Failed to create poll");
    } finally {
      setLoading(false);
    }
  };

  // End a poll
  const handleEndPoll = async (pollId) => {
    if (!window.confirm("Are you sure you want to end this poll?")) {
      return;
    }
    
    try {
      setLoading(true);
      await pollsAPI.endPoll(pollId);
      fetchAllPolls();
    } catch (err) {
      console.error("Error ending poll:", err);
      setError("Failed to end poll");
    } finally {
      setLoading(false);
    }
  };

  // View poll results
  const viewPollResults = async (pollId) => {
    // This would navigate to a results page
    console.log("View results for poll:", pollId);
  };

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <div className="user-info">
          <span>Welcome, {currentUser.name}</span>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </header>

      {error && <div className="error-message">{error}</div>}

      <div className="dashboard-actions">
        <button 
          onClick={() => {
            setShowCreateForm(!showCreateForm);
            setError(""); // Clear any errors when toggling form
          }}
          className="create-poll-btn"
        >
          {showCreateForm ? "Cancel" : "Create New Poll"}
        </button>
      </div>

      {showCreateForm && (
        <div className="create-poll-form">
          <h3>Create New Poll</h3>
          <form onSubmit={handleCreatePoll}>
            <div className="form-group">
              <label htmlFor="title">Poll Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={newPoll.title}
                onChange={handleInputChange}
                required
                minLength={5}
                placeholder="Enter poll title (e.g., 'Class President Election')"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={newPoll.description}
                onChange={handleInputChange}
                required
                placeholder="Provide details about this poll"
              />
            </div>

            <div className="form-group">
              <label htmlFor="question">Question</label>
              <input
                type="text"
                id="question"
                name="question"
                value={newPoll.question}
                onChange={handleInputChange}
                required
                placeholder="Enter the poll question (e.g., 'Who should be the class president?')"
              />
            </div>

            <div className="form-group">
              <label>Options</label>
              {newPoll.options.map((option, index) => (
                <div key={index} className="option-input-group">
                  <input
                    type="text"
                    value={option}
                    onChange={(e) => handleOptionChange(index, e.target.value)}
                    required
                    placeholder={`Option ${index + 1}`}
                  />
                  <button
                    type="button"
                    onClick={() => removeOption(index)}
                    className="remove-option-btn"
                  >
                    âœ•
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={addOption}
                className="add-option-btn"
              >
                + Add Option
              </button>
            </div>

            <button type="submit" disabled={loading} className="submit-poll-btn">
              {loading ? "Creating..." : "Create Poll"}
            </button>
          </form>
        </div>
      )}

      <div className="polls-tabs">
        <div className="tabs-header">
          <button 
            className={activeTab === "ongoing" ? "active" : ""}
            onClick={() => setActiveTab("ongoing")}
          >
            Ongoing Polls
          </button>
          <button 
            className={activeTab === "ended" ? "active" : ""}
            onClick={() => setActiveTab("ended")}
          >
            Ended Polls
          </button>
        </div>

        <div className="polls-list">
          {loading && <p>Loading polls...</p>}
          
          {!loading && activeTab === "ongoing" && (
            <>
              <h3>Ongoing Polls</h3>
              {ongoingPolls.length === 0 ? (
                <p>No ongoing polls found.</p>
              ) : (
                ongoingPolls.map(poll => (
                  <div key={poll.id} className="poll-item ongoing">
                    <div className="poll-info">
                      <h4>{poll.title}</h4>
                      <p className="poll-description">{poll.description}</p>
                      <p className="poll-question"><strong>Question:</strong> {poll.question || "Who do you vote for?"}</p>
                      <div className="poll-dates">
                        <span>Started: {new Date(poll.start_date).toLocaleString()}</span>
                      </div>
                      <div className="vote-count">
                        <span>Votes: {poll.vote_count || 0}</span>
                      </div>
                    </div>
                    
                    <div className="poll-actions">
                      <button 
                        onClick={() => handleEndPoll(poll.id)}
                        className="end-poll-btn"
                        disabled={loading}
                      >
                        End Poll
                      </button>
                      <button 
                        onClick={() => viewPollResults(poll.id)}
                        className="view-results-btn"
                      >
                        View Results
                      </button>
                    </div>
                  </div>
                ))
              )}
            </>
          )}
          
          {!loading && activeTab === "ended" && (
            <>
              <h3>Recently Ended Polls</h3>
              {endedPolls.length === 0 ? (
                <p>No ended polls found.</p>
              ) : (
                endedPolls.map(poll => (
                  <div key={poll.id} className="poll-item ended">
                    <div className="poll-info">
                      <h4>{poll.title}</h4>
                      <p className="poll-description">{poll.description}</p>
                      <p className="poll-question"><strong>Question:</strong> {poll.question || "Who do you vote for?"}</p>
                      <div className="poll-dates">
                        <span>Started: {new Date(poll.start_date).toLocaleString()}</span>
                        {poll.end_date && (
                          <span>Ended: {new Date(poll.end_date).toLocaleString()}</span>
                        )}
                      </div>
                      <div className="vote-count">
                        <span>Total Votes: {poll.vote_count || 0}</span>
                      </div>
                    </div>
                    
                    <div className="poll-actions">
                      <button 
                        onClick={() => viewPollResults(poll.id)}
                        className="view-results-btn"
                      >
                        View Results
                      </button>
                    </div>
                  </div>
                ))
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
