import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';

const PollDetails = () => {
  const { pollId } = useParams();
  const navigate = useNavigate();
  const { user, checkToken } = useAuth();
  
  const [poll, setPoll] = useState(null);
  const [options, setOptions] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [alreadyVoted, setAlreadyVoted] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState([]);
  const [debugInfo, setDebugInfo] = useState(null);

  // Direct fetch for debugging
  const fetchDirectPollDetails = async () => {
    try {
      setLoading(true);
      console.log("Directly fetching poll details for poll ID:", pollId);
      
      if (!pollId || isNaN(parseInt(pollId))) {
        console.error("Invalid poll ID:", pollId);
        setError('Invalid poll ID');
        setLoading(false);
        return;
      }
      
      // Try the debug endpoint that doesn't require authentication
      console.log(`Attempting to access debug endpoint: http://localhost:5000/api/polls/debug/${pollId}`);
      const response = await fetch(`http://localhost:5000/api/polls/debug/${pollId}`);
      
      if (!response.ok) {
        const errorText = await response.text();
        console.error(`Debug endpoint HTTP error ${response.status}: ${errorText}`);
        
        // If we get a 404, try a different URL format
        if (response.status === 404) {
          console.log("Trying alternative debug URL format...");
          const altResponse = await fetch(`http://localhost:5000/api/polls/${pollId}`);
          
          if (!altResponse.ok) {
            const altErrorText = await altResponse.text();
            console.error(`Alternative endpoint HTTP error ${altResponse.status}: ${altErrorText}`);
            throw new Error(`Poll not found with either endpoint (status: ${response.status})`);
          }
          
          const altData = await altResponse.json();
          console.log("Alternative endpoint response:", altData);
          
          if (!altData || !altData.id) {
            console.error("Received invalid poll data from alternative endpoint:", altData);
            throw new Error("Invalid poll data received");
          }
          
          setPoll(altData);
          setOptions(altData.options || []);
          
          if (altData.hasVoted) {
            setAlreadyVoted(true);
            setShowResults(true);
          }
          
          return;
        }
        
        throw new Error(`HTTP error ${response.status}: ${errorText}`);
      }
      
      const data = await response.json();
      console.log("Debug poll details response:", data);
      
      if (!data || !data.id) {
        console.error("Received invalid debug poll data:", data);
        throw new Error("Invalid poll data from debug endpoint");
      }
      
      setDebugInfo(data);
      
      // Use the debug data
      setPoll(data);
      setOptions(data.options || []);
      
      if (data.hasVoted) {
        setAlreadyVoted(true);
        setShowResults(true);
      }
      
      setError(null);
    } catch (err) {
      console.error("Direct fetch poll details error:", err);
      setError(`Poll not found or has been removed. Error: ${err.message}`);
      setPoll(null);
    } finally {
      setLoading(false);
    }
  };
  
  useEffect(() => {
    const fetchPollDetails = async () => {
      try {
        setLoading(true);
        console.log("Fetching poll details for ID:", pollId);
        
        if (!pollId || isNaN(parseInt(pollId))) {
          console.error("Invalid poll ID:", pollId);
          setError('Invalid poll ID');
          setLoading(false);
          return;
        }
        
        try {
          console.log("Attempting to fetch poll with API:", `/polls/${pollId}`);
          const response = await api.get(`/polls/${pollId}`);
          console.log("Poll details response:", response.data);
          
          if (!response.data || !response.data.id) {
            console.error("Received empty or invalid poll data");
            throw new Error("Invalid poll data received");
          }
          
          setPoll(response.data);
          setOptions(response.data.options || []);
          
          // Check if user has already voted
          const userVoteResponse = await api.get(`/votes/user/${pollId}`);
          console.log("User vote response:", userVoteResponse);
          if (userVoteResponse.hasVoted) {
            setAlreadyVoted(true);
            setShowResults(true);
            // Load results automatically if already voted
            fetchResults();
          }
        } catch (apiErr) {
          console.error('API error fetching poll details:', apiErr.message);
          console.log("Attempting fallback to debug endpoint...");
          
          // Fall back to direct fetch
          await fetchDirectPollDetails();
        }
      } catch (err) {
        console.error('Error fetching poll details:', err);
        setError('Failed to load poll details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchPollDetails();
  }, [pollId]);
  
  const fetchResults = async () => {
    try {
      const response = await api.get(`/polls/${pollId}/results`);
      setResults(response.data.options || []);
    } catch (err) {
      console.error('Error fetching results:', err);
      setError('Failed to load results. Please try again later.');
    }
  };
  
  const handleVote = async () => {
    if (!selectedOption) {
      setError('Please select an option to vote');
      return;
    }
    
    try {
      setSubmitting(true);
      setError(null);
      
      await api.post(`/polls/${pollId}/vote`, {
        optionId: selectedOption
      });
      
      setSuccess('Your vote has been recorded successfully!');
      setAlreadyVoted(true);
      setShowResults(true);
      fetchResults();
    } catch (err) {
      console.error('Error submitting vote:', err);
      setError(err.response?.data?.error || 'Failed to submit your vote. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };
  
  const formatDate = (dateString) => {
    if (!dateString) return 'Not set';
    
    const options = { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit', 
      minute: '2-digit'
    };
    
    try {
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (err) {
      console.error('Date formatting error:', err);
      return 'Invalid date';
    }
  };
  
  // Calculate time remaining for a poll
  const getTimeRemaining = (endDate) => {
    if (!endDate) return 'No end date';
    
    try {
      const total = Date.parse(endDate) - Date.parse(new Date());
      if (total <= 0) return 'Ended';
      
      const seconds = Math.floor((total / 1000) % 60);
      const minutes = Math.floor((total / 1000 / 60) % 60);
      const hours = Math.floor((total / (1000 * 60 * 60)) % 24);
      const days = Math.floor(total / (1000 * 60 * 60 * 24));
      
      if (days > 0) return `${days} day${days !== 1 ? 's' : ''} remaining`;
      if (hours > 0) return `${hours} hour${hours !== 1 ? 's' : ''} remaining`;
      if (minutes > 0) return `${minutes} minute${minutes !== 1 ? 's' : ''} remaining`;
      return `${seconds} second${seconds !== 1 ? 's' : ''} remaining`;
    } catch (err) {
      console.error('Time calculation error:', err);
      return 'Unknown time remaining';
    }
  };
  
  const toggleResults = () => {
    if (!showResults) {
      fetchResults();
    }
    setShowResults(!showResults);
  };
  
  if (loading) {
    return <div className="loading">Loading poll details...</div>;
  }
  
  if (!poll) {
    return (
      <div className="container">
        <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>
        <div className="error-message">
          <p>Poll not found or has been removed.</p>
          <button 
            className="retry-btn" 
            onClick={fetchDirectPollDetails}
            style={{ marginTop: '20px', padding: '10px 20px' }}
          >
            Try Direct Fetch
          </button>
        </div>
      </div>
    );
  }
  
  const isPollActive = poll.status === 'ongoing';
  const totalVotes = results && Array.isArray(results) 
    ? results.reduce((sum, option) => sum + (option.vote_count || option.votes || 0), 0) 
    : 0;
  
  return (
    <div className="container">
      <button 
        className="back-btn" 
        onClick={() => navigate(-1)}
      >
        ← Back
      </button>
      
      <div className="poll-details">
        <h3>{poll.title}</h3>
        <p>{poll.description}</p>
        
        <div className="poll-dates">
          <span>Started: {formatDate(poll.start_date)}</span>
          <span>Ends: {formatDate(poll.end_date)}</span>
          {isPollActive && (
            <span className="time-remaining">{getTimeRemaining(poll.end_date)}</span>
          )}
        </div>
        
        <div className="poll-question-box">
          <h4>{poll.question}</h4>
        </div>
        
        {error && (
          <div className="error-message" onClick={() => setError(null)}>
            {error}
          </div>
        )}
        
        {success && (
          <div className="success-message" onClick={() => setSuccess(null)}>
            {success}
          </div>
        )}
        
        <div className="debugging-tools">
          <button 
            className="debug-btn" 
            onClick={fetchDirectPollDetails}
            style={{ fontSize: '12px', padding: '5px 10px', background: '#555' }}
          >
            Debug Fetch
          </button>
        </div>
        
        {debugInfo && (
          <div className="debug-info" style={{ margin: '10px 0', fontSize: '12px' }}>
            <details>
              <summary>Debug Info</summary>
              <pre style={{ maxHeight: '150px', overflow: 'auto' }}>
                {JSON.stringify(debugInfo, null, 2)}
              </pre>
            </details>
          </div>
        )}
        
        {alreadyVoted ? (
          <div className="already-voted">
            <p>You have already cast your vote in this poll.</p>
            {!showResults && (
              <button 
                className="view-results-btn" 
                onClick={toggleResults}
              >
                View Results
              </button>
            )}
          </div>
        ) : (
          <>
            {isPollActive ? (
              <div className="voting-area">
                <div className="options-list">
                  {options && options.length > 0 ? options.map(option => (
                    <div 
                      key={option.id} 
                      className={`option-item ${selectedOption === option.id ? 'selected' : ''}`}
                      onClick={() => setSelectedOption(option.id)}
                    >
                      <div className="option-info">{option.option_text || option.text}</div>
                    </div>
                  )) : <p>No options available for this poll.</p>}
                </div>
                
                <button 
                  className="vote-btn" 
                  onClick={handleVote}
                  disabled={!selectedOption || submitting || !options || options.length === 0}
                >
                  {submitting ? 'Submitting...' : 'Submit Vote'}
                </button>
                
                {!alreadyVoted && (
                  <div style={{ textAlign: 'center', marginTop: '12px' }}>
                    <button 
                      onClick={toggleResults}
                      style={{ 
                        background: 'none', 
                        border: 'none', 
                        color: 'var(--primary-color)',
                        textDecoration: 'underline',
                        cursor: 'pointer',
                        padding: '5px'
                      }}
                    >
                      {showResults ? 'Hide Results' : 'Preview Results'}
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div className="already-voted">
                <p>This poll is {poll.status === 'pending' ? 'not yet started' : 'already ended'}.</p>
                <button 
                  className="view-results-btn" 
                  onClick={toggleResults}
                >
                  {showResults ? 'Hide Results' : 'View Results'}
                </button>
              </div>
            )}
          </>
        )}
        
        {showResults && (
          <div className="results-preview">
            <h4>Results {!poll.status === 'ended' && '(Live)'}</h4>
            <div className="results-list">
              {results && results.length > 0 ? (
                results.map(option => {
                  const voteCount = option.vote_count || option.votes || 0;
                  const percentage = totalVotes > 0 
                    ? Math.round((voteCount / totalVotes) * 100) 
                    : 0;
                    
                  return (
                    <div key={option.id} className="result-item">
                      <span className="option-text">{option.option_text || option.text}</span>
                      <div className="vote-bar">
                        <div 
                          className="vote-progress" 
                          style={{ width: `${percentage}%` }}
                        />
                      </div>
                      <div className="vote-stats">
                        <span className="vote-percentage">{percentage}%</span>
                        <span className="vote-count">({voteCount} vote{voteCount !== 1 ? 's' : ''})</span>
                      </div>
                    </div>
                  );
                })
              ) : (
                <p>No results available yet.</p>
              )}
            </div>
            
            <div className="total-votes">
              Total Votes: <strong>{totalVotes}</strong>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PollDetails;
